import "../style/index.css";

/**
 *  EDIT ONLY INSIDE THIS RENDER FUNCTION
 *  This function is called every time the user changes types or changes any input
 * 
    {
        includeCover: true, // if includeCover is true the algorithm should show the cover image
        background: "data:image/png;base64,", // this is the image's url that will be used as a background for the profile cover
        avatarURL: "https://resources.premierleague.com/premierleague/photos/players/250x250/man51018.png", // this is the url for the profile avatar
        socialMediaPosition: "right", // social media bar position (left or right)
        
        twitter: null, // social media usernames
        github: null,
        linkedin: null,
        instagram: null,

        name: null,
        lastName: null,
        role: null,
        country: null,
        city: null
    }
 */
function render(variables = {}) {
  console.log("These are the current variables: ", variables); // print on the console
  // here we ask the logical questions to make decisions on how to build the html
  // if includeCover==false then we reset the cover code without the <img> tag to make the cover transparent.
  let cover = `<div class="cover"><img src="${variables.background}" /></div>`;
  if (variables.includeCover == false) cover = "<div class='cover'></div>";

  // reset the website body with the new html output
  document.querySelector("#widget_content").innerHTML = `<div class="widget">
            ${cover}
          <img src="${variables.avatarURL}" class="photo" />
          <h1>${variables.name != null ? variables.name : `Mikel`} ${
    variables.lastName != null ? variables.lastName : `Arteta`
  }</h1>
          <h2>${variables.role != null ? variables.role : `Manager`}</h2>
          <h3>${variables.city != null ? variables.city : `London`} ${
    variables.country != null ? variables.country : `UK`
  }</h3>
          <ul class=${
            variables.socialMediaPosition != null
              ? variables.socialMediaPosition
              : `position-left`
          }>
            <li><a href="https://twitter.com/${
              variables.twitter != null ? variables.twitter : `arsenal`
            }"><i class="fab fa-twitter"></i></a></li>
            <li><a href="https://github.com/${
              variables.github != null ? variables.github : `CMcLeodC`
            }"><i class="fab fa-github"></i></a></li>
            <li><a href="https://www.linkedin.com/${
              variables.linkedin != null
                ? variables.linkedin
                : `in/mikel-arteta-901a342a3/`
            }"><i class="fab fa-linkedin"></i></a></li>
            <li><a href="https://www.instagram.com/${
              variables.instagram != null ? variables.instagram : `arsenal/`
            }"><i class="fab fa-instagram"></i></a></li>
          </ul>
        </div>
    `;
}

/**
 * Don't change any of the lines below, here is where we do the logic for the dropdowns
 */
window.onload = function() {
  window.variables = {
    // if includeCover is true the algorithm should show the cover image
    includeCover: true,
    // this is the image's url that will be used as a background for the profile cover
    background:
      "data:image/png;base64,",
    // this is the url for the profile avatar
    avatarURL:
      "https://resources.premierleague.com/premierleague/photos/players/250x250/man51018.png",
    // social media bar position (left or right)
    socialMediaPosition: "position-left",
    // social media usernames
    twitter: null,
    github: null,
    linkedin: null,
    instagram: null,
    name: null,
    lastName: null,
    role: null,
    country: null,
    city: null
  };
  render(window.variables); // render the card for the first time

  document.querySelectorAll(".picker").forEach(function(elm) {
    elm.addEventListener("change", function(e) {
      // <- add a listener to every input
      const attribute = e.target.getAttribute("for"); // when any input changes, collect the value
      let values = {};
      values[attribute] =
        this.value == "" || this.value == "null"
          ? null
          : this.value == "true"
          ? true
          : this.value == "false"
          ? false
          : this.value;
      render(Object.assign(window.variables, values)); // render again the card with new values
    });
  });
};
